const blogSlice = createSlice({
  name: "blog",
  initialState,
  reducers: {
    setPosts: (state, action) => {
      state.posts = action.payload;
    },
    newsLatterPost: (state, action) => {
      state.selectedPostTwo = action.payload;
      state.recommendedPostsTwo = state.posts
      .filter(post => post.id !== action.payload.id) 
        .slice(0, 3); 
    },
    selectPost: (state, action) => {
      state.selectedPost = action.payload;
      state.recommendedPosts = state.posts
        .filter(post => post.id !== action.payload.id) 
        .slice(0, 9);
    },
  },
});

export const { setPosts, selectPost , newsLatterPost } = blogSlice.actions;
export default blogSlice.reducer;
